import React, { useState } from 'react';

const CardList = ({ cards, addToDeck }) => {
  const [deck, setDeck] = useState([]);

  const addToDeckHandler = (card) => {
    if (card.imageUrl) {
      const updatedDeck = [...deck, card.imageUrl];
      setDeck(updatedDeck);
      addToDeck(updatedDeck); // Pass the updated deck to the parent component
      localStorage.setItem('deck', JSON.stringify(updatedDeck));
    }
  };

  const uniqueCardsMap = new Map();

  cards.forEach((card) => {
    if (!uniqueCardsMap.has(card.name)) {
      uniqueCardsMap.set(card.name, card);
    }
  });

  const uniqueCards = Array.from(uniqueCardsMap.values());

  return (
    <div>
      <h2>Search Results</h2>
      <div style={{ display: 'flex', flexWrap: 'wrap' }}>
        {uniqueCards.map((card) => (
          <div key={card.id} style={{ marginRight: '10px', marginBottom: '10px' }}>
            <div>
              <strong>Name:</strong> {card.name} | <strong>Artist:</strong>{' '}
              {card.artist} | <strong>Set:</strong> {card.set} |{' '}
              <strong>Color:</strong> {card.colors ? card.colors.join(', ') : 'N/A'}
            </div>
            {card.imageUrl && (
              <img src={card.imageUrl} alt={card.name} style={{ width: '100px' }} />
            )}
            <button onClick={() => addToDeckHandler(card)}>Add to Deck</button>
          </div>
        ))}
      </div>
      <div>
        <h3>Deck</h3>
        <ul style={{ listStyleType: 'none', padding: 0, display: 'flex', flexWrap: 'wrap' }}>
          {deck.map((imageUrl, index) => (
            <li key={index} style={{ marginRight: '10px', marginBottom: '10px' }}>
              <img src={imageUrl} alt={`Card ${index + 1}`} style={{ width: '100px' }} />
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default CardList;
