import React, { useState } from 'react';

const DeckDisplay = ({ deck }) => (
  <div>
    <h3 style={{ marginBottom: '10px' }}>Deck</h3>
    <ul style={{ listStyleType: 'none', padding: '3px', border: '1px solid #ddd', borderRadius: '8px', display: 'grid', gridTemplateColumns: 'repeat(4, 1fr)', gap: '3px' }}>
      {deck.map((imageUrl, index) => (
        <li key={index} style={{ borderRadius: '8px', overflow: 'hidden' }}>
          <img src={imageUrl} alt={`Card ${index + 1}`} style={{ width: '50%'}} />
        </li>
      ))}
    </ul>
  </div>
);

const CardList = ({ cards }) => {
  const [deck, setDeck] = useState([]);
  const [filteredCards, setFilteredCards] = useState([]);

  const addToDeck = (card) => {
    if (card.imageUrl) {
      const updatedDeck = [...deck, card.imageUrl];
      setDeck(updatedDeck);
      localStorage.setItem('deck', JSON.stringify(updatedDeck));
    }
  };

  const filterResults = (query) => {
    const filtered = uniqueCards.filter(
      (card) =>
        card.name.toLowerCase().includes(query.toLowerCase()) ||
        (card.colors && card.colors.join(', ').toLowerCase().includes(query.toLowerCase()))
    );
    setFilteredCards(filtered);
  };

  const uniqueCardsMap = new Map();

  cards.forEach((card) => {
    if (!uniqueCardsMap.has(card.name)) {
      uniqueCardsMap.set(card.name, card);
    }
  });

  const uniqueCards = Array.from(uniqueCardsMap.values());

  const drawRandomCards = (count) => {
    const drawnCards = [];
    const remainingCards = filteredCards.length > 0 ? filteredCards : uniqueCards;

    for (let i = 0; i < count; i++) {
      const randomIndex = Math.floor(Math.random() * remainingCards.length);
      const randomCard = remainingCards[randomIndex];

      if (randomCard) {
        drawnCards.push(randomCard);
        remainingCards.splice(randomIndex, 1);
      }
    }

    drawnCards.forEach((card) => addToDeck(card));
  };

  return (
    <div>
      <h2>Search Results</h2>
      <div>
        <input
          type="text"
          placeholder="Filter results..."
          onChange={(e) => filterResults(e.target.value)}
          style={{ marginBottom: '10px' }}
        />
      </div>
      <div style={{ display: 'flex', alignItems: 'flex-start' }}>
        <div style={{ flex: 1 }}>
          {/* ... (your card list rendering logic) */}
          <button
            onClick={() => drawRandomCards(7)}
            style={{
              marginTop: '10px',
              background: '#007bff',
              color: 'white',
              padding: '8px 12px',
              borderRadius: '4px',
              cursor: 'pointer',
              border: 'none',
            }}
          >
            Draw 7 Random Cards
          </button>
          <button
            onClick={() => drawRandomCards(1)}
            style={{
              marginTop: '10px',
              marginLeft: '10px',
              background: '#007bff',
              color: 'white',
              padding: '8px 12px',
              borderRadius: '4px',
              cursor: 'pointer',
              border: 'none',
            }}
          >
            Draw 1 Random Card
          </button>
        </div>
        <div style={{ flex: 1 }}>
          <DeckDisplay deck={deck} />
        </div>
      </div>
    </div>
  );
};

export default CardList;
