import React, { useState } from 'react';

const DeckDisplay = ({ deck }) => (
  <div>
    <h3>Deck</h3>
    <ul style={{ listStyleType: 'none', padding: 0, display: 'grid', gridTemplateColumns: 'repeat(5, 1fr)', gap: '5px' }}>
      {deck.map((imageUrl, index) => (
        <li key={index}>
          <img src={imageUrl} alt={`Card ${index + 1}`} style={{ width: '80px' }} />
        </li>
      ))}
    </ul>
  </div>
);

const CardList = ({ cards }) => {
  const [currentPage, setCurrentPage] = useState(1);
  const cardsPerPage = 20;

  const indexOfLastCard = currentPage * cardsPerPage;
  const indexOfFirstCard = indexOfLastCard - cardsPerPage;
  const currentCards = cards.slice(indexOfFirstCard, indexOfLastCard);

  const totalPages = Math.ceil(cards.length / cardsPerPage);

  const handlePageChange = (page) => {
    setCurrentPage(page);
  };

  return (
    <div>
      <h2>Search Results</h2>
      <div style={{ display: 'flex' }}>
        <div style={{ flex: 1 }}>
          <ul>
            {currentCards.map((card) => (
              <li key={card.id}>
                <div>
                  <strong>Name:</strong> {card.name} | <strong>Artist:</strong>{' '}
                  {card.artist} | <strong>Set:</strong> {card.set} |{' '}
                  <strong>Color:</strong> {card.colors ? card.colors.join(', ') : 'N/A'}
                </div>
                {card.imageUrl && (
                  <img src={card.imageUrl} alt={card.name} style={{ width: '80px' }} />
                )}
                <button onClick={() => addToDeck(card)}>Add to Deck</button>
              </li>
            ))}
          </ul>
          <div>
            <button onClick={() => handlePageChange(currentPage - 1)} disabled={currentPage === 1}>
              Previous Page
            </button>
            <span> Page {currentPage} of {totalPages} </span>
            <button onClick={() => handlePageChange(currentPage + 1)} disabled={currentPage === totalPages}>
              Next Page
            </button>
          </div>
        </div>
        <div style={{ flex: 1 }}>
          <DeckDisplay deck={deck} />
        </div>
      </div>
    </div>
  );
};

export default CardList;
